package org.song.java8;

/**
 * Purpose of this class is to
 */
public class OneEditDistance {
    public boolean isOneEditDistance(String s, String t) {
        if (s == null || s.length()==0 ){
            return t==null ? false : t.length() == 1;
        }
        if (t == null || t.length() ==0){
            return s==null? false : s.length() ==1;
        }

        int min = Math.min(s.length(), t.length());
        int max = Math.max(s.length(), t.length());
        if (max - min > 1){
            return false;
        }


        int distance = 0;
        int i=0;
        int j=0;

        while (i<s.length() && j<t.length()){
            char cs = s.charAt(i);
            char ct = t.charAt(j);
            if (s.charAt(i) == t.charAt(j)){
                i++;
                j++;
            }
            else{
                if (i+1<s.length() && j+1 < t.length() && s.charAt(i+1) == t.charAt(j+1)){
                    i++;
                    j++;
                    distance++;

                }
                else if (i+1<s.length() && s.charAt(i+1) == ct){
                    i++;
                    distance++;
                }
                else if(j+1<t.length() && cs == t.charAt(j+1)){
                    j++;
                    distance++;
                }

            }


            if (distance > 1){
                return false;
            }
        }

        return distance ==1;
    }

    public static void main(String[] args){
        OneEditDistance oed  = new OneEditDistance();
        String s = "";
        String t = "";
        System.out.print("the result is: "+ oed.isOneEditDistance(s, t));
    }
}
